import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-card-price',
  templateUrl: './card-price.component.html',
  styleUrls: ['./card-price.component.css']
})
export class CardPriceComponent {

  @Input()
  gameType:string =''
  @Input()
  gamePrice:string=''
  @Input()
  gameCTA:string=''
}
<div class="card-price__container">
  <div class="card-price__content">
    <p class="card-price__title">{{gameCTA}}</p>
    <div class="flex">
      <div class="card-price__value__console flex">
        <span>|</span>
        <p>{{gameType}}</p>
        <div>| PS4</div>
      </div>
      <div class="flex">
        <div class="card-price__value__money">{{gamePrice}}</div>
      </div>
    </div>
  </div>
</div>
