import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {


  /* function toggleMenu () {
    document.getElementById('nav-secondary').classList.toggle('active');
    document.getElementById('line-off').classList.toggle('openMenu');
    document.getElementById('line-up').classList.toggle('openMenu');
    document.getElementById('line-down').classList.toggle('openMenu');
    document.getElementById('close-menu').classList.toggle('active');
  } */

  toogleMenuActive:boolean = true;

  toogleMenu() {
    if (this.toogleMenuActive) {
      this.toogleMenuActive = false;
    } else {
      this.toogleMenuActive = true;
    }
  }
}
<header class="header__container">
  <div class="container flex">

    <div class="header__logo">
      <a href=""><img src="assets/ps-logo.png" alt=""></a>
    </div>

    <div class="header__sandwich flex" (click)="toogleMenu()" [ngClass]="toogleMenuActive?'enable':'disable'" title="Botão para abrir o menu"> <!-- Hamburguer menu -->
      <div class="line" id="line-up"></div>
      <div class="line" id="line-off"></div>
      <div class="line" id="line-down"></div>
    </div>

    <div class="header__item">
      <ul class="flex">
        <li><a href="">Youtube</a></li>
        <li><a href="">Linkedin</a></li>
        <li><a href="">Github</a></li>
      </ul>
    </div>

    <!-- <div id="close-menu" (click)="toogleMenu()" [ngClass]="toogleMenuActive?'enable':'disable'"></div> --> <!-- Para fechar menu quando clicar fora (mobile) -->


  </div>
</header>
