import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent implements OnInit {

  @Input()
  gameCover:string =''
  @Input()
  gameLabel:string=''
  @Input()
  gameType:string =''
  @Input()
  gamePrice:string=''
  @Input()
  gameCTA:string=''

  ngOnInit(): void {
  }
}
<div class="card__container">
  <a href="">
    <app-card-img gameCover="{{gameCover}}"></app-card-img>
    <app-card-label gameLabel="{{gameLabel}}"></app-card-label>
    <app-card-price gameCTA="{{gameCTA}}" gameType="{{gameType}}" gamePrice="{{gamePrice}}"></app-card-price>
  </a>
</div>
